import React, { Component, useState } from 'react';
/*import { Link } from "react-router-dom";*/
import './Checkin.css';
import logo from '../assets/logo.jpg';


class Checkin extends Component {
    state = {
        find: '',
        queryPesquisa: '',
        dados : [
            { id: 1, cpf:'45805412030', nome: 'Jose Maria', status:0},
            { id: 2, cpf:'80055465120', nome: 'Jose Silva', status:0},
            { id: 3, cpf:'70036611547', nome: 'Math RMHj Jkskdjfks skdjfskjf ksjfskj', status:1},
            { id: 4, cpf:'70036618707', nome: 'Lucas Luis do Reis Filho Mezenga Matuzalem', status:0},            
        ]        
    };        
     
    constructor(props) {
        super(props);    
        setTimeout(() => {
          this.setState({ 
            dados: [ 
              ...this.state.dados,
              { id: 5, cpf:'54487987120', nome:'Teste', status:0 } // Adicionando um item
            ] 
          });
        });
      }  
  
    handleSubmit = e => {
        e.preventDefault();  
        console.log(this.state.find);
        console.log(this.state.queryPesquisa);      
        
        let ocultaPesquisa = document.querySelector('.form-pesquisa');
        ocultaPesquisa.style.display = 'none';  
        
        let mostraResultado = document.querySelector('.result');
        mostraResultado.style.display = 'block';

        let principal = document.querySelector('.form-checkin');        
        principal.style.height = '700px';
        principal.style.width = '800px';
        principal.style.marginTop = '300px'; 
    }

    handleChange = e => {
        this.setState({ [e.target.name]: [e.target.value]});        
    }
        
    inputNome = () => {
        let find = document.getElementById('find');
        find.setAttribute('placeholder', 'Pesquise pelo Nome');     
        
        let query = document.getElementById('queryPesquisa');
        query.setAttribute('value', 'queryNome');
        console.log(this.queryPesquisa);
    }

    inputCpf = () => {
        let find = document.getElementById('find');
        find.setAttribute('placeholder', 'Pequise pelo CPF');

        let query = document.getElementById('queryPesquisa');
        query.setAttribute('value', 'queryCpf');
        console.log(this.queryPesquisa);
    }
  

    voltarPesquisa = () => {
        let renderPesquisa = document.querySelector('.form-pesquisa');
        renderPesquisa.style.display = 'block';                         
        
        let ocultaResultado = document.querySelector('.result');
        ocultaResultado.style.display = 'none';                 
        
        let defaultForm = document.querySelector('.form-checkin');
        defaultForm.style.width=  '600px';
        defaultForm.style.height= '400px';   
        defaultForm.style.marginTop = '1px';                
    }   
    
    confirmaCheckin = (id) => {                
        const newDados = this.state.dados.map(res => {
            return res.id == id ? { ...res, status: 1} : res;
        })
        //console.log(newDados);        
        this.state.dados = newDados;        
        //console.log(this.state.dados);        
        this.setState({dados: newDados});
    }

    desfazCheckin = (id) => {
        const newDados = this.state.dados.map(res => {
            return res.id == id ? { ...res, status: 0} : res;
        })
        //console.log(newDados);        
        this.state.dados = newDados;        
        //console.log(this.state.dados);        
        this.setState({dados: newDados});
    }
  
render(){
    return (
        <div id="principal">
            <div className="form-checkin">                                                                    
            <img src={logo} alt="Eventos Logo" />                               
            <div className="form-pesquisa">
            <form onSubmit={this.handleSubmit}>
                 <button type="button" className="form-checkin-button" onClick={this.inputNome}>Nome</button>                                        
                <button type="button" className="form-checkin-button" onClick={this.inputCpf}>CPF</button>
               
               <input type="hidden" name="queryPesquisa" id="queryPesquisa"
                onChange={this.handleChange} value={this.state.queryPesquisa} />                   

                <div className="check-find">
                    <input type="text" name="find" id="find" placeholder="Pesquise pelo nome" 
                     onChange={this.handleChange} value={this.state.find} />               

                    <button className="enviar" type="submit" className="form-checkin-button" /*onClick={this.buscaParticipante}*/>Pesquisar</button>  
                </div>
                </form>    
            </div>                
                <div className="result" style={{display: 'none'}}> 
                    <h2 className="titulo">Resultados:</h2>                   
                    <table id="resultado">                      
                        <tbody>
                            {//this.resultadoPesquisa()                                                        
                            this.state.dados.map(res => (           
                                //const {id, cpf, nome, status } = res;                                                      
                                <tr key={res.id}>
                                    <td>
                                        <div className="dados-participantes">
                                            <strong>{res.nome.toUpperCase()}</strong>
                                            <span>{res.cpf}</span>
                                        </div>                
                                        <input type="hidden" value={res.id}></input>
                                    </td>
                                    <td>
                                        <div className="status-inscricao">
                                            <span>Status:</span>
                                            <strong className="status-resultado">{res.status===0 ? 'Pendente': 'Confirmado'}</strong>
                                        </div>                                        
                                    </td>
                                    <td>
                                        { res.status=== 0 ? 
                                                <button className="button-check"  
                                                className="confirma-checkin" 
                                                onClick={() => this.confirmaCheckin(res.id)}>Confirmar</button> 
                                        
                                                :
                                                <button className="desfaz-checkin" 
                                                onClick={() => this.desfazCheckin(res.id)}>Desfazer</button> }                                              
                                    </td>                                      
                                </tr>                          
                                ))                      
                            }
                            
                        </tbody>              
                     </table>  
                     <button className="button-voltar" onClick={this.voltarPesquisa}>Voltar</button>                
                </div>
            </div>
        </div>
    )
}
}

export default Checkin;
