import React, { Component, useState } from 'react';
import { Link } from "react-router-dom";
import './Checkin.css';
import logo from '../assets/logo.jpg';
//class Checkin extends Component {

export default function Checkin() {      
    const [pesquisa, setPesquisa] = useState('');


    var dados = [
        { id: 1, cpf:'45805412030', nome: 'Jose Maria', status:1},
        { id: 2, cpf:'80055465120', nome: 'Jose Silva', status:0},
        { id: 3, cpf:'70036611107', nome: 'Math RMHj Jkskdjfks skdjfskjf ksjfskj', status:1},
        { id: 4, cpf:'70036611107', nome: 'Lucas Luis do Reis Filho Mezenga Matuzalem Ferreira', status:0},
        
    ]; 
    

    function inputNome(){
        let find = document.getElementById('find');
        find.setAttribute('placeholder', 'Digite o Nome');        
    }

    function inputCpf(){
        let find = document.getElementById('find');
        find.setAttribute('placeholder', 'Digite o CPF');
    }

    function buscaParticipante(e){        
        console.log(pesquisa);
        let ocultaPesquisa = document.querySelector('.form-pesquisa');
        ocultaPesquisa.style.display = 'none';  
        
        let mostraResultado = document.querySelector('.result');
        mostraResultado.style.display = 'block';

        let principal = document.querySelector('.form-checkin');
        principal.style.textAlign = 'left'; 
        principal.style.height = '900px';
        principal.style.marginTop = '300px';  
    }

    function confirmaCheckin(id){
        console.log(id);
        const participante = dados.filter(user => (user.id == id));
        
        console.log(participante);
        
       
    }

    function desfazCheckin(){
        console.log('Desfaz');
    }

    function resultadoPesquisa(){        
        return dados.map((res, index) => {            
            const {id, cpf, nome, status } = res;                      
            return(
               /* <tr key={id}>
                    <td>{cpf}</td>
                    <td>{nome}</td>
                  {//  <td><button>Checkin</button></td> 
                  }
                </tr>              
                */                                                  
                <li className="lista-participante">
                    <div className="dados-status">
                        <div className="dados-participantes">
                            <strong>{nome}</strong>
                            <span>{cpf}</span>
                        </div>                
                        <hidden value={id}></hidden>
                
                         <div className="status-inscricao">
                            <span>Status:</span>
                            <strong className="status-resultado">{status==0 ? 'Pendente': 'Confirmado'}</strong>
                        </div>
                    </div> 
                    {status==0 ? <button className="button-check" onClick={() => confirmaCheckin(id)}>Confirmar</button>  :
                    <button className="button-check" onClick={desfazCheckin}>Desfazer</button> }                                              
                </li>
            )
        })
    }

    return (
        <div id="principal">
            <div className="form-checkin">                                                        
            <img src={logo} className="logo" alt="Eventos Logo" />                   
            <div className="form-pesquisa">    
                <button type="submit" onClick={inputNome}>Nome</button>                                        
                <button type="submit" onClick={inputCpf}>CPF</button>  
                <div className="check-find">
                    <input type="text" id="find" value={pesquisa}
                        onChange={e => setPesquisa(e.target.value)}
                    />
                    <button className="enviar" type="submit" onClick={buscaParticipante}>Pesquisar</button>  
                </div>
            </div>
                
                <div className="result" style={{display: 'none'}}>                    
                   {/* <table id="resultado"> */}
                        {resultadoPesquisa()}
                        {/*<tbody>
                            {resultadoPesquisa()}
                        </tbody>   */}               
                    {/* </table> */}
                                                        
                </div>

            </div>
        </div>
    );
  
}

//export default Checkin;
