import React, { Component, useState } from 'react';
import { Link } from "react-router-dom";
import './Checkin.css';
import logo from '../assets/logo.jpg';
//class Checkin extends Component {

export default function Checkin() {      
    const [pesquisa, setPesquisa] = useState('');

    function inputNome(){
        let find = document.getElementById('find');
        find.setAttribute('placeholder', 'Digite o Nome');        
    }

    function inputCpf(){
        let find = document.getElementById('find');
        find.setAttribute('placeholder', 'Digite o CPF');
    }

    function buscaParticipante(e){        
        console.log(pesquisa);
        let ocultaPesquisa = document.querySelector('.form-pesquisa');
        ocultaPesquisa.style.display = 'none';  
        
        let mostraResultado = document.querySelector('.result');
        mostraResultado.style.display = 'block';
    }

    function resultadoPesquisa(){
        var resultado = [
            { id: 1, cpf:'45805412030', nome: 'Jose Maria'},
            { id: 2, cpf:'80055465120', nome: 'Jose Silva'},
            { id: 1, cpf:'70036611107', nome: 'Math'},
        ]; 

        return resultado.map((res, index) => {
            const {id, cpf, nome } = res
            return(
                <tr key={id}>
                    <td>{cpf}</td>
                    <td>{nome}</td>
                </tr>
            )
        })

    }

    return (
        <div id="principal">
            <div className="form-checkin">                                                        
            <div className="form-pesquisa">    
                <button type="submit" onClick={inputNome}>Nome</button>                                        
                <button type="submit" onClick={inputCpf}>CPF</button>  
                <div className="check-find">
                    <input type="text" id="find" value={pesquisa}
                        onChange={e => setPesquisa(e.target.value)}
                    />
                    <button className="enviar" type="submit" onClick={buscaParticipante}>Pesquisar</button>  
                </div>
            </div>
                
                <div className="result" style={{display: 'none'}}>                    
                    <table id="resultado">
                        <tbody>
                            {resultadoPesquisa()}
                        </tbody>                 
                    </table>
                                                        
                </div>

            </div>
        </div>
    );
  
}

//export default Checkin;
