import React, {Component} from 'react';

class QueryNome extends Component {
    state = {
        find: '',
        queryPesquisa: 'queryName',
        dados : [
            { id: 1, cpf:'45805412030', nome: 'Jose Maria', status:0},
            { id: 2, cpf:'80055465120', nome: 'Jose Silva', status:0},
            { id: 3, cpf:'70036611107', nome: 'Math Card', status:1},
            { id: 4, cpf:'70036618707', nome: 'Lucas Luis do Reis Filho Mezenga Matuzalem', status:0},            
        ]        
    };  
    
   /* consulta = () => {
        const consulta = this.state.dados.map(res => {   //const {id, cpf, nome, status } = res;                                                                                                                            
            return res.nome == find ? consulta : <strong>Não encontrado</strong>;
        })
    }*/

    render(){
        return(
            <tbody> 
            { 
       
                <tr key={res.id}>
                    <td>
                        <div className="dados-participantes">
                            <strong>{res.nome.toUpperCase()}</strong>
                            <span>{res.cpf}</span>
                        </div>                
                        <input type="hidden" value={res.id}></input>
                    </td>
                    <td>
                        <div className="status-inscricao">
                            <span>Status:</span>
                            <strong className="status-resultado">{res.status===0 ? 'Pendente  ': 'Confirmado'}</strong>
                        </div>                                        
                    </td>
                    <td>
                        {   res.status === 0 
                            ? 
                            <button className="confirma-checkin" onClick={() => this.confirmaCheckin(res.id)}>Confirmar</button> 
                            :   
                            <button className="desfaz-checkin" onClick={() => this.desfazCheckin(res.id)}>Desfazer</button> }                                              
                    </td>                                      
                </tr>                          
            //))
            }   
            </tbody>              
        );
    }


}

export default QueryNome;
